sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    // Check if the Ticket Status is being set to 'Closed'
    if (current.ticket_status == 'Closed') {  // Replace 'ticket_status' with the correct field name

        // Get the related Vehicle Tracker record
        var vehicleTrackerGR = new GlideRecord('x_1426255_lva_vehicle_tracker');  // Replace with the correct table name
        if (vehicleTrackerGR.get(current.x_1426255_lva_vehicle_tracker)) {  // Assuming 'x_1426255_lva_vehicle_tracker' is the reference field to Vehicle Tracker

            // Set the Vehicle Status of the Tracker to match the Vehicle Status on the Servicing ticket
            vehicleTrackerGR.vehicle_status = current.u_vehicle_status;  // Replace 'u_vehicle_status' with the correct field name

            // Set the Ticket Status of the Vehicle Tracker to 'Closed'
            vehicleTrackerGR.ticket_status = 'Closed';  // Replace 'ticket_status' with the correct field name on Vehicle Tracker

            // Update the Vehicle Tracker record
            vehicleTrackerGR.update();
        }
    }

})(current, previous);
