sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    // Check if there is an existing open Vehicle Servicing ticket associated with this Vehicle Tracker
    var servTicketGR = new GlideRecord('x_1426255_lva_vehicle_servicing');  // Replace with the correct table name for Vehicle Servicing
    servTicketGR.addQuery('x_1426255_lva_vehicle_tracker', current.sys_id);  // Assuming 'x_1426255_lva_vehicle_tracker' is the reference field to Vehicle Tracker
    servTicketGR.addQuery('ticket_status', 'Open');  // Assuming 'ticket_status' is the status field on Vehicle Servicing
    servTicketGR.query();

    if (!servTicketGR.hasNext()) {
        // No open servicing ticket found, create a new one
        var newServTicketGR = new GlideRecord('x_1426255_lva_vehicle_servicing');  // Replace with the correct table name for Vehicle Servicing
        newServTicketGR.initialize();

        newServTicketGR.x_1426255_lva_vehicle_tracker = current.sys_id;  // Map Vehicle Tracker to the new servicing ticket
        newServTicketGR.vehicle = current.vehicle;  // Map Vehicle from Vehicle Tracker to the new servicing ticket
        newServTicketGR.vehicle_status = 'In Service';  // Set Vehicle Status to In Service
        newServTicketGR.date_sent_for_servicing = new GlideDateTime();  // Set Date Sent for Servicing to current date/time
        newServTicketGR.ticket_status = 'Open';  // Set Ticket Status to Open

        // Insert the new servicing ticket record
        newServTicketGR.insert();
    }

})(current, previous);
