sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    // Get the related Vehicle record
    var vehicleGR = new GlideRecord('vehicle');
    if (vehicleGR.get(current.vehicle)) { // Assuming 'vehicle' is the reference field to the Vehicle table

        // Determine the Vehicle Status from Vehicle Tracker and set the status on the Vehicle record
        switch (current.vehicle_status) {  // Replace 'vehicle_status' with the correct field name for Vehicle Status
            case 'Pending Release':
            case 'Out on Field':
            case 'Returned for Inspection':
            case 'Sent for Servicing':
                vehicleGR.status = 'Unavailable'; // Replace 'status' with the correct field name on the Vehicle table
                break;

            case 'Returned to Warehouse':
                vehicleGR.status = 'Available';
                break;

            case 'Decommissioned':
                vehicleGR.status = 'Decommissioned';
                break;

            default:
                // Handle other cases or do nothing
                break;
        }

        // Update the Vehicle record
        vehicleGR.update();
    }

})(current, previous);
